module.exports = {
  registerAuction: function(input, params) {
    var auctions = input.auctions;
    auctions[input.count++] = {
      id: input.count++,
      ownerAddress: params.ownerAddress,
      auctionName: params.auctionName,
      endTime: params.endTime,
      bids: []
    };
  },
  bidAuction: function(input, params) {
    var auction = input.auctions[params.id];
    auction.bids.push({
      bidderAddress: params.bidderAddress,
      bidAmount: params.bidAmount,
      bidTime: Date.now()
    });
  },
  reportAuction: function(input) {
    return input.auctions.length;
  }
}
